package javapractice;

public class Ifstatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//If the sum is 100, write a code to show that the sum is hundred. 
		//
		
		int sum = 100;
		
		if (sum >= 100) {
			System.out.println("Sum is greater 100");
		} else if (sum < 100) {
			System.out.println("Sum is less 100");
		} else {
			System.out.println("Sum is not 100");
		}
	}

}
